from test_support import (
    clean,
    gnatprove,
    default_refiners_no_sort,
)

# Check error messages generated by gnatprove

configs = ["good.gpr", "bad_profile.gpr", "bad_pep.gpr"]

for i in configs:
    print("analysis of " + i)
    print("--------------------------")
    gnatprove(
        opt=["-P", i, "--mode=check"],
        refiners=default_refiners_no_sort(),
    )
    print("")
    clean()
